import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Button from '@mui/material/Button';
import './fonts.css';
import { background, secondary } from './colors.js';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  circle: {
    position: 'absolute',
    top: '80%',
    left: theme.spacing(8),
    transform: 'translateY(-50%)',
    width: '75px',
    height: '75px',
    borderRadius: '50%',
    backgroundColor: secondary, 
  },
}));

const buttonTheme = {
  color: secondary,
  fontWeight: 'bold',
  fontFamily: 'Montserrat',
  fontSize: '0.75em',
  letterSpacing: '0.1em',
  marginTop: '20px',
  marginRight: '50px',
  display: 'block'
};
const firstLetter = { fontSize: '1.3em' };

function SimpleAppBar() {
  const [loading, setLoading] = React.useState(false);
  const [success, setSuccess] = React.useState(false);
  const timer = React.useRef();

  const buttonSx = {
    ...(success && {
      bgcolor: green[500],
      '&:hover': {
        bgcolor: green[700],
      },
    }),
  };

  const classes = useStyles();
  return (
      <AppBar position="static" sx={{ background: background, boxShadow: 'none' }}>
        <Toolbar sx={{ display: 'flex', justifyContent: 'flex-end' }}>
          <Button sx={{ ...buttonTheme, marginRight: '10px' }}>
            <span style={firstLetter}>C</span>ontact
          </Button>
          <Button sx={{ ...buttonTheme, marginRight: '10px' }}>
            <span style={firstLetter}>L</span>inks
          </Button>
          <Button sx={buttonTheme}>
            <span style={firstLetter}>R</span>esume
          </Button>
        </Toolbar>
      </AppBar>
  );
}

export default SimpleAppBar;
