import React from 'react';
import { secondary, titleTextColor } from './colors.js';
import { makeStyles } from '@material-ui/core/styles';
import { Typography, Button } from '@mui/material';

const useStyles = makeStyles((theme) => ({
  content: {
    fontFamily: 'Montserrat',
    display: 'block',
    textAlign: 'right',
    marginTop: '10%',
    marginRight: '12%',
  },
  titleText: {
    color: titleTextColor,
    fontWeight: 'bold',
    letterSpacing: '0.3em'
  },
  descrTextTheme: {
    color: titleTextColor,
    fontWeight: '10',
    fontSize: '0.75em',
    letterSpacing: '0.15em',
    fontStyle: 'italic',
    fontSize: '2.75em',
  },
  // add left to  move horizontally
  buttonClass:{
    color: 'green',
    fontFamily: 'Montserrat',
    fontSize: '1', 
    display: 'flex',
    position: 'absolute',
    top: '20px', 
    borderRadius: '5px',
    width: 'auto'
  },
  buttonStyle:{
    backgroundColor: "#21b6ae",
}
}));

function TitleContent() {
  const classes = useStyles();
  return (
    <div className={classes.content}>
      <div className={classes.titleText}>
        <span style={{ fontSize: '4em' }}>E</span>
        <span style={{ fontSize: '3.5em' }}>AN  </span>
        <span style={{ fontSize: '4em' }}>V</span>
        <span style={{ fontSize: '3.5em' }}>ANDERGRAAF</span>
      </div>
      <div className={classes.descrTextTheme}>
        Software Engineer
      </div>
      {/* <Button className={classes.buttonClass}  style={{backgroundColor: "#21b6ae"}} variant="contained">About Me </Button> */}
    </div>
  );
}

export default TitleContent;
